// Obtener hora actual
var now = new Date();
var timestamp = now.toTimeString().split(' ')[0]; // formato HH:MM:SS

// Convertir payload a texto si no lo es
var payloadStr = (typeof msg.payload === 'object') ? JSON.stringify(msg.payload) : String(msg.payload);

// Agregar timestamp al mensaje
msg.payload = "[" + timestamp + "] " + payloadStr;

return msg;

var states = msg.payload.state;

return [
    { payload: states[0] ? true : false },
    { payload: states[1] ? true : false },
    { payload: states[2] ? true : false },
    { payload: states[3] ? true : false }
];

let mode = global.get("in_modo_seleccionado");
if (mode === undefined) {
    node.warn("Modo no seleccionado");
    return null;
}

let mensaje = {
    task: "gpio_input",
    input: [0, 1, 2, 3],
    mode: mode
};

msg.payload = JSON.stringify(mensaje);
return msg;

let mode = global.get("out_modo_seleccionado");

if (mode === undefined) {
    node.warn("Modo no seleccionado");
    return null;
}

let mensaje = {
    task: "gpio_output",
    output: [0, 1, 2, 3],
    mode: mode
};

if (mode === 1) {
    let state = [
        flow.get("gpio_0") || 0,
        flow.get("gpio_1") || 0,
        flow.get("gpio_2") || 0,
        flow.get("gpio_3") || 0
    ];
    mensaje.state = state;
}

msg.payload = mensaje;
return msg;

var states = msg.payload.state;

return [
    { payload: states[0] ? true : false },
    { payload: states[1] ? true : false },
    { payload: states[2] ? true : false },
    { payload: states[3] ? true : false }
];

// Genera 1024 muestras de datos aleatorios para cada canal
var ch0 = [];
var ch1 = [];
var ch2 = [];
var ch3 = [];

for (var i = 0; i < 1024; i++) {
    ch0.push(Math.floor(Math.random() * 4096)); // 0-4095
    ch1.push(Math.floor(Math.random() * 4096));
    ch2.push(Math.floor(Math.random() * 4096));
    ch3.push(Math.floor(Math.random() * 4096));
}

msg.payload = {
    "task": "adc",
    "channels": {
        "ch0": ch0,
        "ch1": ch1,
        "ch2": ch2,
        "ch3": ch3
    }
};

return msg;

// Separamos los datos en 4 mensajes
var payload = msg.payload;

if (payload.task === "adc" && payload.channels) {
    let ch0 = payload.channels.ch0 || [];
    let ch1 = payload.channels.ch1 || [];
    let ch2 = payload.channels.ch2 || [];
    let ch3 = payload.channels.ch3 || [];

    return [
        { payload: ch0, topic: "ch0" },
        { payload: ch1, topic: "ch1" },
        { payload: ch2, topic: "ch2" },
        { payload: ch3, topic: "ch3" }
    ];
}

return null;

var ena = msg.payload.ena;

return [
    { payload: ena[0] ? true : false },
    { payload: ena[1] ? true : false },
    { payload: ena[2] ? true : false },
    { payload: ena[3] ? true : false }
];

let mode = global.get("motor_get_modo_seleccionado");
if (mode === undefined) {
    node.warn("Modo no seleccionado");
    return null;
}

let mensaje = {
    task: "motor_get",
    motor: [0, 1, 2, 3],
    mode: mode
};

msg.payload = JSON.stringify(mensaje);
return msg;

let dir = msg.payload.dir;

if (!Array.isArray(dir) || dir.length !== 4) {
    node.warn("Esperado array de 4 posiciones");
    return null;
}

return [
    { payload: dir[0] === 0 ? "Sentido horario" : "Sentido antihorario", topic: "dir0" },
    { payload: dir[1] === 0 ? "Sentido horario" : "Sentido antihorario", topic: "dir1" },
    { payload: dir[2] === 0 ? "Sentido horario" : "Sentido antihorario", topic: "dir2" },
    { payload: dir[3] === 0 ? "Sentido horario" : "Sentido antihorario", topic: "dir3" }
];

let step = msg.payload.step_time;

if (!Array.isArray(step) || step.length !== 4) {
    node.warn("Esperado array de 4 posiciones en 'step_time'");
    return [null, null, null, null];
}

// Cada mensaje va a una salida distinta
return [
    { payload: step[0] },
    { payload: step[1] },
    { payload: step[2] },
    { payload: step[3] }
];

let mode = global.get("motor_modo_seleccionado");

if (mode === undefined) {
    node.warn("Modo no seleccionado");
    return null;
}

// Obtenemos los datos de los otros campos (por ejemplo desde flow)
let ena = [
    flow.get("ena_0") || 0,
    flow.get("ena_1") || 0,
    flow.get("ena_2") || 0,
    flow.get("ena_3") || 0
];

let dir = [
    flow.get("dir_0") || 0,
    flow.get("dir_1") || 0,
    flow.get("dir_2") || 0,
    flow.get("dir_3") || 0
];

let step_time = [
    flow.get("step_0") || 1000,
    flow.get("step_1") || 1000,
    flow.get("step_2") || 1000,
    flow.get("step_3") || 1000
];

let mensaje = {
    task: "motor_set",
    motor: [0, 1, 2, 3],
    ena: ena,
    dir: dir,
    step_time: step_time,
    mode: mode
};

msg.payload = mensaje;
return msg;

let t = context.get('t') || 0;
const samples = 10;

let ch0 = [], ch1 = [], ch2 = [], ch3 = [];
for (let i = 0; i < samples; i++) {
    ch0.push(Math.sin(0.1 * (t + i)) * 100 + 100);
    ch1.push(Math.sin(0.1 * (t + i) + 1) * 100 + 100);
    ch2.push(Math.sin(0.1 * (t + i) + 2) * 100 + 100);
    ch3.push(Math.sin(0.1 * (t + i) + 3) * 100 + 100);
}

context.set('t', t + samples);
msg.payload = {
    channels: {
        ch0, ch1, ch2, ch3
    }
};
return msg;

let config = {
    task: "adc",
    sample_rate: flow.get("sample_rate") || 10,
    num_samples: flow.get("num_samples") || 32,
    buffer_size: flow.get("buffer_size") || 16,
    mode: flow.get("mode") || 0,
    enable_external_trigger: flow.get("enable_external_trigger") ? 1 : 0
};

msg.payload = config;
return msg;
